const body = $json.body || $json;
const task_id = body.task_id;

if (!task_id) {
  throw new Error('Missing required field: task_id');
}

return [{ json: { task_id } }];

const task = Array.isArray($json) ? $json[0] : $json;

if (!task || !task.id) {
  throw new Error('Task not found');
}

const prompt = `Analyze this task and suggest the next best action.
Task: ${task.title} | Category: ${(task.tags || []).join(', ')} | Priority: ${task.priority_score || 5}/10
Description: ${task.description || 'None'} | Due: ${task.due_date || 'No due date'}

Respond ONLY as JSON (no markdown, no code fences): { "next_action": "...", "reasoning": "...", "action_type": "review|followup|complete_task" }`;

return [{ json: { task_id: task.id, task_title: task.title, prompt } }];

const response = $json.response || '';
const taskData = $('Build Ollama Prompt').item.json;

// Extract JSON from response - handle possible markdown fences or think tags
let parsed;
try {
  // Try direct parse first
  parsed = JSON.parse(response);
} catch (e) {
  // Try extracting JSON from response text
  const jsonMatch = response.match(/\{[\s\S]*?"next_action"[\s\S]*?\}/)
  if (jsonMatch) {
    try {
      parsed = JSON.parse(jsonMatch[0]);
    } catch (e2) {
      parsed = {
        next_action: 'Review task and determine next steps',
        reasoning: 'Could not parse AI response - manual review recommended',
        action_type: 'review'
      };
    }
  } else {
    parsed = {
      next_action: response.substring(0, 200),
      reasoning: 'Raw AI response (JSON parsing failed)',
      action_type: 'review'
    };
  }
}

return [{
  json: {
    task_id: taskData.task_id,
    task_title: taskData.task_title,
    next_action: parsed.next_action,
    reasoning: parsed.reasoning,
    action_type: parsed.action_type || 'review'
  }
}];

const proposal = Array.isArray($json) ? $json[0] : $json;
const taskData = $('Parse Ollama Response').item.json;

return [{
  json: {
    success: true,
    proposal_id: proposal.id,
    task_title: taskData.task_title,
    next_action: taskData.next_action,
    action_type: taskData.action_type
  }
}];
